import {redirect, useParams,useRouteLoaderData} from 'react-router-dom'
import EventItem from '../components/EventItem';


function EventDetailPage(){

const data=useRouteLoaderData('event-detail');

return <>
<EventItem event={data.event}/>
</> 

   
    

}

export default EventDetailPage;

export async function loader({request,params}) {
 
   const id= params.id;
 
 const response=await fetch('http://localhost:8080/events/' + id);  

if(!response.ok){
throw new Response(JSON.stringify({message:'could not fetch data'}),{status:500});

}

return response;
} 

export async function action({params,request}){

const eventId=params.eventId;
  const response=fetch('http://localhost:8080/events/'+eventId,
 {
  method:request.method,
}
  );

if(!response.ok){
throw new Response(JSON.stringify({message:'could not delete data'}),{status:500});

}

return redirect('/events');

}
